#!/usr/bin/env sh

cd ./des

echo 'Current brances list:'
echo

git branch
echo

read -p "[?] - Enter the branch name to switch to: " branch_name
echo

branch_switched() {
	git checkout $branch_name 
}


if ! branch_switched; then
	echo
	echo "[!] - Failed to switch to the branch $branch_name in public repository. See the error details above."

	exit 1
else
	echo
	echo "Successfully switched to the branch '$branch_name'!"

	exit 0
fi
